// CHECK-NEXT:    br i1 [[EXITCOND_NOT]], label [[FOR_COND_CLEANUP]], label [[FOR_BODY]], {{!llvm.loop ![0-9]+}}
//
int loop_access_by_dereference(int *__counted_by(n) a, int n) {
	int *b = a;
	int r = 0;
	for (int i = 0; i < n; ++i) {
		r += *b++;
	}
	return r;
}

