// CHECK-NEXT:    br i1 [[EXITCOND_NOT]], label [[FOR_COND_CLEANUP]], label [[FOR_BODY]], {{!llvm.loop ![0-9]+}}
//
void loop_accesses_eliminate_second_lower_check(int* __counted_by(n) dst, unsigned n) {
  for (int i = 0; i < n; i += 1) {
    dst[i+1] = 0;
    dst[i+2] = 0;
  }
}

