#include <stdint.h>

int main()
{
  int N = 100;

  double a[N];
  uint32_t b[N];
  float c[N];
  uint64_t d[N];

  for (int i=0; i<N; i++) {
    a[i]=i;
    b[i] = i+1;
    c[i] = i+2;
    d[i] = i+3;
  }

  double sum1 = 0.0;
  uint32_t sum2 = 0;
  float sum3 = 0;
  uint64_t sum4 = 0;
  double sum5 = 0;

#pragma omp target teams distribute parallel for reduction(+:sum1, sum2, sum3, sum4)
  for (int j = 0; j< N; j=j+1) {
    sum1 += a[j];
    sum2 += b[j];
    sum3 += c[j];
    sum4 += d[j];
  }

#pragma omp target teams distribute parallel for reduction(+:sum5) if(target: N == 1000)
  for (int j = 0; j< N; j=j+1)
    sum5 += a[j];
}
