void array_section_no_length_map_clause(float *d, int index) {
  float **f;

  #pragma omp target map(tofrom : d[:])
  {
    d[3] += 2;
  }

  #pragma omp target map(to : d[2:])
  {
    d[3] += 3;
  }

  #pragma omp target map(alloc : f[index][:])
  {
    f[index][2] += 4;
  }

  #pragma omp target map(tofrom : f[index][index+1:])
  {
    f[index][index] += 5;
  }
}
