! Tests rewriting of OUT_OF_RANGE()
! REQUIRES: target=x86-64{{.*}}
! REQUIRES: system-linux
! RUN: %flang_fc1 -fdebug-unparse -cpp %s 2>&1 | FileCheck %s --check-prefixes=CHECK%if target=x86_64{{.*}} %{%if system-linux %{,CHECK-X86-64%}%}

logical round

#define T1(XT,XK,MT,MK) \
block; \
  XT(XK) x; \
  MT(MK) mold; \
  print *, #XT, XK, #MT, MK, out_of_range(x,mold); \
end block

#define T2(XT,XK,MT,MK) \
block; \
  XT(XK) x; \
  MT(MK) mold; \
  print *, #XT, XK, #MT, MK, 'round', out_of_range(x,mold,round); \
end block

#define INTMOLDS(M,XT,XK) \
  M(XT,XK,integer,1); \
  M(XT,XK,integer,2); \
  M(XT,XK,integer,4); \
  M(XT,XK,integer,8); \
  M(XT,XK,integer,16)

#define REALMOLDS(M,XT,XK) \
  M(XT,XK,real,2); \
  M(XT,XK,real,3); \
  M(XT,XK,real,4); \
  M(XT,XK,real,8); \
  M(XT,XK,real,10); \
  M(XT,XK,real,16)

#define INTXS(M1,M2) \
  M1(M2, integer, 1); \
  M1(M2, integer, 2); \
  M1(M2, integer, 4); \
  M1(M2, integer, 8); \
  M1(M2, integer, 16)

#define REALXS(M1,M2) \
  M1(M2, real, 2); \
  M1(M2, real, 3); \
  M1(M2, real, 4); \
  M1(M2, real, 8); \
  M1(M2, real, 10); \
  M1(M2, real, 16)

INTXS(INTMOLDS, T1)
INTXS(REALMOLDS, T1)
REALXS(INTMOLDS, T1)
REALXS(INTMOLDS, T2)
REALXS(REALMOLDS, T1)

end

