//
// NextSiliconIRFixup pass fixes the caller/callee ABI discrepancies
// generated by the Fortran front-end, fixes TLS variable access, and
// adds XRay instrumentation attributes to functions (if not already added).
//
//===------------------------------------------------------------------===//

#ifndef LLVM_TRANSFORMS_UTILS_NEXTSILICONIRFIXUP_H
#define LLVM_TRANSFORMS_UTILS_NEXTSILICONIRFIXUP_H

#include "llvm/IR/PassManager.h"

namespace llvm {

class Module;
class ModulePass;

class NextSiliconIRFixupPass : public PassInfoMixin<NextSiliconIRFixupPass> {
public:
  NextSiliconIRFixupPass() {}

  PreservedAnalyses run(Module &M, ModuleAnalysisManager &AM);
};

} // end namespace llvm

#endif // LLVM_TRANSFORMS_UTILS_NEXTSILICONIRFIXUP_H